class RandomUniqueWords:
    def __init__(self, words):
        random.shuffle(words)
        self.words = words
        self.index = 0
        self.used_words = set()

    def __call__(self):
        while self.index < len(self.words):
            word = self.words[self.index]
            self.index += 1
            if word not in self.used_words:
                self.used_words.add(word)
                return word
        raise StopIteration("No more unique words.")

# Set to keep track of generated sets of 12 words
generated_sets = set()

# Function to generate a unique set of 12 words
def genrat():
    while True:
        ruw = RandomUniqueWords(mnemonic_list)
        selected_words = set()
        while len(selected_words) < 12:
            word = ruw()
            selected_words.add(word)

        # Convert the set of selected words to a tuple and check if it's unique
        selected_tuple = tuple(sorted(selected_words))
        if selected_tuple not in generated_sets:
            generated_sets.add(selected_tuple)
            return " ".join(selected_words)

